var fs = require('fs');

/*
*对于地区代码的操作
* 包括读取和更新
 */
exports.area = (function(){
    var arear = JSON.parse(fs.readFileSync(__dirname+"/../../shared/area-code"));
    function update(obj){
        arear = obj;
        fs.writeFileSync(_dirname+"/../../shared/area-code",arear);
        return JSON.stringify({code:0}) ;
    }
    function get_area(){
        return arear;
    }
    return{
        update:update,
        get:get_area
    }
})();

